changeSet.sql:
ALTER TABLE DocumentVersion ADD COLUMN description TEXT;
ALTER TABLE DocumentVersion ADD COLUMN createdByUserId BIGINT;
ALTER TABLE DocumentVersion ADD CONSTRAINT fk_DocumentVersion_SystemUser
FOREIGN KEY (createdByUserId) REFERENCES SystemUser (id);

INSERT INTO Document
(id, fileUUID, name, isShared, isRemoved, webVisibility, description, collegeId, angelId, created, modified)
select bi.id, bi.fileUUID, bi.name, 1, 0, bi.isWebVisible, NULL, bi.collegeId, bi.angelId, bi.created, bi.modified from BinaryInfo bi;

INSERT INTO DocumentVersion
(id, documentId, versionId, fileName, byteSize, mimeType, timestamp, thumbnail, pixelWidth, pixelHeight, created, modified, angelId, collegeId, description, createdByUserId, filePath)
select bi.id, bi.id, NULL, bi.name, bi.byteSize, bi.mimeType, bi.created, NULL, bi.pixelWidth, bi.pixelHeight, bi.created, bi.modified, bi.angelId, bi.collegeId, NULL, NULL, bi.filePath from BinaryInfo bi;

UPDATE BinaryInfoRelation SET documentId = binaryInfoId;

ALTER TABLE WebURLAlias ADD COLUMN redirectTo VARCHAR(512);
ALTER TABLE WebURLAlias DROP FOREIGN KEY WebURLAlias_webNode;
ALTER TABLE WebURLAlias MODIFY webNodeId bigint(20);
ALTER TABLE WebURLAlias ADD CONSTRAINT WebURLAlias_webNode FOREIGN KEY (webNodeId) REFERENCES WebNode (id) ON DELETE CASCADE;
